                                <input class=\"uv-field\" type=\"email\" name=\"email\">
                            </div>
                        </div>

                        <!-- CSRF token Field -->
                        <input type=\"hidden\" name=\"_token\" />
                        <!-- //CSRF token Field -->

                        <button class=\"uv-btn\">{{ 'Send Mail'|trans }}</button>
                    </div>
                </section>
            </form>
        </div>

    </div>
